       counter === 20 ? alert("Counter Reached Its Maximum Value !!") : counter = counter + 1;
       setCounter(counter);
    }
    const remove = () =>{
        counter === 0 ? alert("Counter value is Zero !!") : counter = counter -1;
        setCounter(counter);
    }
return(
    <>
    <div style="color:blue;">
    Hello World Component !!
    <br />
    <button onClick={add}>Add btn</button>
    <br />
    Total : {counter}
    <br />
    <button onClick={remove}>Remove btn</button>
    </div>
    </>

);
}

