import { useState } from "react";
function MyComponent() {
  console.log("testing");

  return (
    <div>
      <br></br>
    </div>
  );
}

const HelloWorld = () => {
  let [counter, setCounter] = useState(0);
  //  let  counter = 0;
  const add = () => {
    counter === 20
      ? alert("Counter Reached Its Maximum Value !!")
      : (counter = counter + 1);
    setCounter(counter);
  };
  const remove = () => {
    counter === 0 ? alert("Counter value is Zero !!") : (counter = counter - 1);
    setCounter(counter);
  };
  return (
    <>
      <div style={{ color: "red", textAlign: "center" }}>
        Hello World Component !!
        <br />
        <button onClick={add}>Add btn</button>
        <br />
        <p>Total : {counter}</p>
        <br />
        <button onClick={remove}>Remove btn</button>
      </div>
    </>
  );
};

export { HelloWorld, MyComponent };
